import { useState } from "react";

import classes from "./Comments.module.css";
import NewCommentForm from "./NewCommentForm";
import CommentsList from "./CommentsList";
import useHttp from "../../hooks/use-http";
import { useParams } from "react-router-dom";

const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);

  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };
  //////////
  const params = useParams();
  const { isLoading, error, requestFn: sendQuote } = useHttp();
  const addCommentHandler = (commentText) => {
    sendQuote({
      url: "https://react-router-fcb08-default-rtdb.firebaseio.com/quotes.json",
      method: "POST",
      body: { id: params.quoteId, text: commentText },
    });
  };
  ///////////////
  console.log(params.quoteId);
  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className="btn" onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && <NewCommentForm onAddComment={addCommentHandler} />}
      <p>Comments...</p>
      <CommentsList comments={[{ id: "c1", text: "nice" }]} />
    </section>
  );
};

export default Comments;
