import React, { Component, useEffect, useState } from "react";

import QuoteList from "../components/quotes/QuoteList";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import useHttp from "../hooks/use-http";

const AllQuotes = (props) => {
  const quots = [
    { id: "q1", author: "Sayed", text: "No One Remain!" },
    { id: "q2", author: "Arab", text: "Yahya Alarab!" },
  ];
  const { isLoading, error, requestFn: getQuotes } = useHttp();
  const [allQuots, setAllQuots] = useState(null);
  useEffect(() => {
    const transformData = (data) => {
      const loadedArray = [];
      Object.entries(data).map(([key, value]) => {
        return loadedArray.push({
          id: key,
          text: value.text,
          author: value.author,
        });
      });
      setAllQuots(loadedArray);
    };
    getQuotes(
      {
        url: "https://react-router-fcb08-default-rtdb.firebaseio.com/quotes.json",
      },
      transformData
    );
  }, [getQuotes]);
  console.log(allQuots);
  return (
    <div>
      <QuoteList quotes={quots}></QuoteList>
    </div>
  );
};

export default AllQuotes;
