import { Route, useParams, Link } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
const quots = [
  { id: "q1", author: "Sayed", text: "No One Remain!" },
  { id: "q2", author: "Arab", text: "Yahya Alarab!" },
];
const QuoteDetails = () => {
  const params = useParams();
  const quote = quots.find((quote) => quote.id === params.quoteId);
  console.log(quote, "kk");

  //   if (!quote) {
  //     return <p> No Quote Found</p>;
  //   }
  return (
    <div>
      {quote ? (
        <HighlightedQuote text={quote.text} author={quote.author} />
      ) : (
        <NoQuotesFound />
      )}

      <Route path={`/all-quotes/${params.quoteId}`}>
        <div className="centered">
          <Link
            className="btn--flat"
            to={`/all-quotes/${params.quoteId}/comments`}
          >
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path={`/all-quotes/${params.quoteId}/comments`}>
        <Comments></Comments>
      </Route>
    </div>
  );
};
export default QuoteDetails;
