import { useEffect, useState } from "react";

import classes from "./Comments.module.css";
import NewCommentForm from "./NewCommentForm";
import CommentsList from "./CommentsList";
import useHttp from "../../hooks/use-http";
import {
  useHistory,
  useLocation,
  useParams,
  useRouteMatch,
} from "react-router-dom";
import LoadingSpinner from "../ui/LoadingSpinner";

const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);

  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };
  //////////
  const params = useParams();
  const history = useHistory();
  const match = useRouteMatch();
  const location = useLocation();
  const { isLoading, error, requestFn: sendComment } = useHttp();
  ////////////
  const addCommentHandler = (commentText) => {
    sendComment({
      url: "https://react-router-fcb08-default-rtdb.firebaseio.com/comments.json",
      method: "POST",
      body: { id: params.quoteId, text: commentText },
    });
    // history.replace(location.pathname);
  };
  const addCommentFnHandler = (commentText) => {
    sendComment({
      url: "https://react-router-fcb08-default-rtdb.firebaseio.com/comments.json",
      method: "POST",
      body: { id: params.quoteId, text: commentText },
    });
    // history.replace(location.pathname);
  };

  ///////////////
  /////////
  const { isLoading: isload, error: err, requestFn: getComments } = useHttp();
  const [allComments, setAllComments] = useState([]);
  useEffect(() => {
    const transformData = (data) => {
      const loadedArray = [];
      Object.entries(data).map(([key, value]) => {
        return loadedArray.push({
          id: value.id,
          text: value.text,
        });
      });
      setAllComments(loadedArray);
    };
    getComments(
      {
        url: "https://react-router-fcb08-default-rtdb.firebaseio.com/comments.json",
      },
      transformData
    );
  }, [getComments]);

  let comments;
  if (isload) {
    comments = (
      <div className="centered">
        <LoadingSpinner />{" "}
      </div>
    );
  }
  if (!isload && !err && allComments.length > 0) {
    comments = <CommentsList comments={allComments} />;
  }
  if (!isload && !err && allComments.length === 0) {
    comments = <p className="centered">no comments were added yet</p>;
  }
  //////////
  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className="btn" onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && (
        <NewCommentForm
          isLoading={isLoading}
          onAddComment={addCommentHandler}
          error={error}
          addCommentFn={addCommentFnHandler}
        />
      )}
      {comments}
    </section>
  );
};

export default Comments;
