import { useEffect, useState } from "react";
import {
  Route,
  useParams,
  Link,
  useHistory,
  useLocation,
  useRouteMatch,
} from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import useHttp from "../hooks/use-http";
import LoadingSpinner from "../components/ui/LoadingSpinner";
const quots = [
  { id: "q1", author: "Sayed", text: "No One Remain!" },
  { id: "q2", author: "Arab", text: "Yahya Alarab!" },
];

const QuoteDetails = () => {
  const params = useParams();
  //////////
  const { isLoading, error, requestFn: getQuotes } = useHttp();
  const [allQuots, setAllQuots] = useState([]);
  useEffect(() => {
    const transformData = (data) => {
      const loadedArray = [];
      Object.entries(data).map(([key, value]) => {
        return loadedArray.push({
          id: key,
          text: value.text,
          author: value.author,
        });
      });
      setAllQuots(loadedArray);
    };
    getQuotes(
      {
        url: "https://react-router-fcb08-default-rtdb.firebaseio.com/quotes.json",
      },
      transformData
    );
  }, [getQuotes]);
  /////////
  const quote = allQuots.find((quote) => quote.id === params.quoteId);
  // console.log(quote, "kk");

  //   if (!quote) {
  //     return <p> No Quote Found</p>;
  //   }
  const history = useHistory();
  const location = useLocation();
  const match = useRouteMatch();
  // console.log(history);
  // console.log(location);
  // console.log(match);

  if (isLoading) {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  // if (error) {
  //   return <p className="centered focused">{error}</p>;
  // }
  // if (!error && !isLoading && quote.text.trim() === "") {
  //   return <p>No Quote Found</p>;
  // }

  return (
    <div>
      {quote ? (
        <HighlightedQuote text={quote.text} author={quote.author} />
      ) : (
        <NoQuotesFound />
      )}
      <Route path={`${match.path}`} exact>
        <div className="centered">
          <Link className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </Link>
        </div>
      </Route>
      {/* {location.pathname === `/all-quotes/${params.quoteId}` && (
        <div className="centered">
          <Link
            className="btn--flat"
            to={`/all-quotes/${params.quoteId}/comments`}
          >
            Load Comments
          </Link>
        </div>
      )} */}
      <Route path={`${match.path}/comments`}>
        <Comments></Comments>
      </Route>
    </div>
  );
};
export default QuoteDetails;
